mapper.insert:
		INSERT INTO ATT_TWEET(TID, UUID, T_TYPE, REF_TID, CONTENT, IMAGE_LINK)
		VALUES(SEQ_TWEET.NEXTVAL, #{uuid},
		#{t_type}
        DEFAULT, 
		#{ref_tid}
        NULL,
		#{content}, 
		#{image_link}
        NULL
        )

mapper.update:
		UPDATE ATT_TWEET
		SET CONTENT = #{content}, IMAGE_LINK = 
		#{image_link}
        NULL, 
        UPDATEDATE = SYSDATE
		WHERE TID =#{tid}

mapper.select:
SELECT * FROM ( SELECT /*+ INDEX_DESC(ATT_TWEET, SYS_C0011086)*/ ROWNUM RN, TID, UUID, T_TYPE, REF_TID, CONTENT, IMAGE_LINK, REGDATE, UPDATEDATE FROM ATT_TWEET WHERE T_TYPE = 'nomal' AND
ROWNUM <= #{pageNum} * #{amount}
)
WHERE RN > (#{pageNum}-1) * #{amount}
